$mysqli = new mysqli("hostname", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\nfor ($i = 0; $i < $some_limit; $i++) {\n    $time = time();\n\n    // Delete statement with prepared statement\n    $stmt = $mysqli->prepare("DELETE FROM $storetbl WHERE ? - datecreated >= 432000");\n    $stmt->bind_param("i", $time);\n    $stmt->execute();\n    if ($stmt->error) {\n        die("Query failed: " . $stmt->error);\n    }\n    $stmt->close();\n\n    // Assuming you fetch data in $row array\n    // Item link modifications\n    if ($row['link'] != null) {\n        $shortlink = substr($row['link'], 0, 30) . '...';\n    }\n\n    if ($row['itemsize'] != null) {\n        $mensshirt = $row['itemsize'];\n    }\n\n    if ($row['waistsize'] != 0) {\n        $waistsize = $row['waistsize'];\n        $w = 'Waist';\n    }\n\n    if ($row['lengthsize'] != 0) {\n        $lengthsize = $row['lengthsize'];\n        $l = 'Length';\n    }\n\n    if ($row['ladystopsize'] != null) {\n        $ladystopsize = $row['ladystopsize'];\n    }\n\n    if ($row['ladysdresssize'] != 0) {\n        $ladysdresssize = $row['ladysdresssize'];\n    }\n\n    if ($row['ladysbottomsize'] != 0) {\n        $ladysbottomsize = $row['ladysbottomsize'];\n    }\n\n    if ($row['shoesize'] != 0) {\n        $shoesize = $row['shoesize'];\n    }\n\n    echo "$storename";\n    echo "Item Number : {$row['itemnum']} ";\n    echo "Discount Price: $${row['DiscountedPrice']} ";\n    echo "Item Type : {$row['itemtype']}";\n    echo "Item Size :\n    $mensshirt \n    $waistsize $w \n    $lengthsize $l \n    $ladystopsize \n    $ladysdresssize \n    $ladysbottomsize \n    $shoesize\n    ";\n    echo "Item Color: {$row['itemcolor']} ";\n\n    if ($row['link'] != null) {\n        echo "Item URL: $shortlink";\n    }\n\n    // STATUS\n    if ($row['VillainID'] == 0) {\n        echo "Status: Unclaimed ";\n    } else {\n        $stmt = $mysqli->prepare("SELECT PaypalEmail FROM villains WHERE VillainID = ?");\n        $stmt->bind_param("i", $row['VillainID']);\n        $stmt->execute();\n        $stmt->bind_result($VillainPaypal);\n        $stmt->fetch();\n        $stmt->close();\n\n        echo "Status: Claimed by $VillainPaypal";\n    }\n}\n\n$mysqli->close();