<?php\n// Database connection using PDO\n$dsn = 'mysql:host=your_host;dbname=your_db;charset=utf8';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare the SQL statement with placeholders\n    $stmt = $pdo->prepare("SELECT p_pid, p_name, p_cat FROM products WHERE p_sid = :p_sid AND p_name LIKE :p_name LIMIT 1");\n\n    // Bind parameters\n    $p_sid = 346;\n    $p_name = "%product name here%"; // Adjust the wildcards as necessary\n    $stmt->bindParam(':p_sid', $p_sid, PDO::PARAM_INT);\n    $stmt->bindParam(':p_name', $p_name, PDO::PARAM_STR);\n\n    // Execute the query\n    $stmt->execute();\n\n    // Fetch the result\n    $result = $stmt->fetch(PDO::FETCH_ASSOC);\n    if ($result) {\n        print_r($result);\n    } else {\n        echo "No results found.";\n    }\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}\n?>