<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$current_post_id = 1; // Change this to the ID of the current post\n$sql = "SELECT post_text FROM posts WHERE id = ?";\n$stmt = $conn->prepare($sql);\n$stmt->bind_param("i", $current_post_id);\n$stmt->execute();\n$stmt->bind_result($current_post_text);\n$stmt->fetch();\n$stmt->close();\n\n$sql = "SELECT id, post_text, post_slug,\n        MATCH(post_text) AGAINST(?) AS score\n        FROM posts\n        WHERE MATCH(post_text) AGAINST(?)\n        AND id != ?\n        ORDER BY score DESC\n        LIMIT 5";\n\n$stmt = $conn->prepare($sql);\n$stmt->bind_param("ssi", $current_post_text, $current_post_text, $current_post_id);\n$stmt->execute();\n$result = $stmt->get_result();\n\n$related_posts = [];\nwhile ($row = $result->fetch_assoc()) {\n    $related_posts[] = $row;\n}\n\n$stmt->close();\n$conn->close();\n\nforeach ($related_posts as $post) {\n    echo "Title: " . $post['post_text'] . "<br>";\n    echo "Slug: " . $post['post_slug'] . "<br><br>";\n}\n?>