use Spatie\Url\Url;\n\n// Example URL\n$printPageURL = "http://www.somesite.com/print.php?newUser=1";\n\n// Parse the URL\n$url = Url::fromString($printPageURL);\n\n// Add or update a parameter\n$url = $url->withQueryParameter('mode', '1');\n\n// Convert back to string\n$redirectURL = (string) $url;\n\necho $redirectURL; // Outputs: http://www.somesite.com/print.php?newUser=1&mode=1