<?php\n// Set the username and password\n$valid_username = 'your_username';\n$valid_password = 'your_password';\n\n// Check if the username and password are provided\nif (!isset($_SERVER['PHP_AUTH_USER']) || !isset($_SERVER['PHP_AUTH_PW'])) {\n    header('WWW-Authenticate: Basic realm="Restricted Area"');\n    header('HTTP/1.0 401 Unauthorized');\n    echo 'Authentication required';\n    exit;\n}\n\n// Validate the username and password\nif ($_SERVER['PHP_AUTH_USER'] !== $valid_username || $_SERVER['PHP_AUTH_PW'] !== $valid_password) {\n    header('WWW-Authenticate: Basic realm="Restricted Area"');\n    header('HTTP/1.0 401 Unauthorized');\n    echo 'Authentication failed';\n    exit;\n}\n\n// Generate the RSS feed\nheader('Content-Type: application/rss+xml; charset=utf-8');\n\necho '<?xml version="1.0" encoding="UTF-8" ?>';\necho '<rss version="2.0">';\necho '<channel>';\necho '<title>Intranet Document Management System</title>';\necho '<link>http://yourdomain.com/rss</link>';\necho '<description>Latest documents</description>';\n\n// Fetch and output your feed items here\n\necho '</channel>';\necho '</rss>';\n?>