if (!is_uploaded_file($tmp_name)) {\n       echo json_encode(array(\n           'success' => false,\n           'errno' => 'Not an uploaded file.'\n       ));\n       exit;\n   }\n\n   if (!file_exists($tmp_name)) {\n       echo json_encode(array(\n           'success' => false,\n           'errno' => 'Temporary file does not exist.'\n       ));\n       exit;\n   }\n\n   $ret = move_uploaded_file($tmp_name, $orig_path);\n\n   if ($ret) {\n       echo json_encode(array(\n           'success' => true\n       ));\n   } else {\n       echo json_encode(array(\n           'success' => false,\n           'errno' => 'move_uploaded_file failed with error: ' . json_encode(error_get_last())\n       ));\n   }\n   exit;