<?php\nfunction remove_html_tags($text) {\n    // This pattern matches HTML tags with irregular spaces\n    $pattern = '/<\s*\/?\s*([a-zA-Z0-9]+)(\s+[a-zA-Z0-9-_]+(\s*=\s*("[^"]*"|\'[^\']*\'|[^\'">\s]+))?)*\s*\/?\s*>/i';\n    \n    // Use preg_replace to remove the matched HTML tags\n    $clean_text = preg_replace($pattern, '', $text);\n    \n    return $clean_text;\n}\n\n// Example usage\n$html_content = '< p > blah blah blah < / p > < a href= " link.html " > blah blah blah < /a >';\n$clean_content = remove_html_tags($html_content);\necho $clean_content;\n?>