<?php\n\n// Define the path to your Drupal installation.\ndefine('DRUPAL_ROOT', '/path/to/drupal');\n\n// Include the bootstrap file to initialize Drupal.\nrequire_once DRUPAL_ROOT . '/includes/bootstrap.inc';\n\n// Bootstrap Drupal to the session phase.\ndrupal_bootstrap(DRUPAL_BOOTSTRAP_SESSION);\n\n// Get the global user object.\nglobal $user;\n\n// Print the user ID of the logged-in user.\nif ($user->uid) {\n  echo 'Logged-in user ID: ' . $user->uid;\n} else {\n  echo 'No user is logged in.';\n}\n?>