<?php\n// Assuming you have a PDO connection $pdo\nif (isset($_GET['key'])) {\n    $activationKey = $_GET['key'];\n\n    // Verify the activation key from the database\n    $sql = "SELECT * FROM users WHERE activation_key = :activation_key";\n    $stmt = $pdo->prepare($sql);\n    $stmt->bindParam(':activation_key', $activationKey);\n    $stmt->execute();\n\n    if ($stmt->rowCount() > 0) {\n        // Activation key is valid, activate the user account\n        $user = $stmt->fetch();\n        $sql = "UPDATE users SET is_active = 1 WHERE activation_key = :activation_key";\n        $stmt = $pdo->prepare($sql);\n        $stmt->bindParam(':activation_key', $activationKey);\n        $stmt->execute();\n\n        echo "Account activated successfully.";\n    } else {\n        // Invalid activation key\n        echo "Invalid activation key.";\n    }\n} else {\n    echo "No activation key provided.";\n}\n?>