$fileName = $_POST['textFile'];\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $_SESSION['serverURL'] . "fileReader.php?textFile=" . urlencode($fileName));\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\ncurl_setopt($ch, CURLOPT_TIMEOUT, 10); // Set a timeout of 10 seconds\n$result = curl_exec($ch);\n\nif(curl_errno($ch)) {\n    echo 'Error:' . curl_error($ch);\n} else {\n    echo $result;\n}\n\ncurl_close($ch);