function dmsToDecimal($deg, $min, $sec, $hemisphere) {\n    $decimal = $deg + ($min / 60) + ($sec / 3600);\n\n    // If the hemisphere is South or West, the value should be negative\n    if ($hemisphere == 'S' || $hemisphere == 'W') {\n        $decimal *= -1;\n    }\n\n    return $decimal;\n}\n\n// Example coordinates: 37° 14′ 6″ N, 115° 48′ 40″ W\n$latitude = dmsToDecimal(37, 14, 6, 'N');\n$longitude = dmsToDecimal(115, 48, 40, 'W');\n\necho "Latitude: $latitude\n";\necho "Longitude: $longitude\n";