<?php\n// Define the path to the JSON file\n$filepath = "../uploads/james.json";\n\n// Read the content of the file\n$jsonContent = file_get_contents($filepath);\n\n// Ensure the content is in UTF-8 encoding\n$jsonContent = utf8_encode($jsonContent);\n\n// Decode the JSON data\n$decodedData = json_decode($jsonContent, true);\n\n// Check if decoding was successful\nif (json_last_error() !== JSON_ERROR_NONE) {\n    die("Invalid JSON data: " . json_last_error_msg());\n}\n\n// Check if the 'uri' key exists in the decoded array\nif (isset($decodedData['uri']) && is_array($decodedData['uri'])) {\n    foreach ($decodedData['uri'] as $uri) {\n        // Output each URI\n        echo htmlspecialchars($uri, ENT_QUOTES, 'UTF-8') . "<br>";\n    }\n} else {\n    echo "No URI data found.";\n}\n\n// Function to fix encoding to UTF-8\nfunction fixEncoding($in_str) {\n    $cur_encoding = mb_detect_encoding($in_str);\n    if ($cur_encoding == "UTF-8" && mb_check_encoding($in_str, "UTF-8")) {\n        return $in_str;\n    } else {\n        return utf8_encode($in_str);\n    }\n}\n?>