$itemList = array();\n$xmlReader = new XMLReader();\n$xmlReader->open('XMLFILE');\n$i = 0;\n$itemOpen = false;\n\nwhile($xmlReader->read()) {\n    if($xmlReader->nodeType == XMLReader::ELEMENT) {\n        switch ($xmlReader->localName) {\n            case 'item':\n                $itemOpen = true;\n                $itemList[$i] = array();\n                break;\n            case 'title':\n                if ($itemOpen) {\n                    $xmlReader->read();\n                    $itemList[$i]['title'] = $xmlReader->value;\n                }\n                break;\n            case 'description':\n                if ($itemOpen) {\n                    $xmlReader->read();\n                    $itemList[$i]['description'] = $xmlReader->value;\n                }\n                break;\n            case 'media:thumbnail':\n                if ($itemOpen) {\n                    $itemList[$i]['media:thumbnail'] = $xmlReader->getAttribute('url');\n                }\n                break;\n        }\n    } elseif ($xmlReader->nodeType == XMLReader::END_ELEMENT && $xmlReader->localName == 'item') {\n        $itemOpen = false;\n        $i++;\n    }\n}\n\n$xmlReader->close();