<?php\nfunction convertToHtml($input) {\n    // Sanitize input to prevent XSS attacks\n    $safeInput = htmlspecialchars($input, ENT_QUOTES, 'UTF-8');\n\n    // Convert newlines to <br> tags\n    $htmlOutput = nl2br($safeInput);\n\n    // Wrap in <p> tags for paragraphs\n    $htmlOutput = '<p>' . str_replace("\n", '</p><p>', $htmlOutput) . '</p>';\n\n    return $htmlOutput;\n}\n\n// Example usage\n$userInput = "This is a paragraph.\nAnd this is another paragraph.";\n$htmlContent = convertToHtml($userInput);\necho $htmlContent;\n?>