<?php\nrequire 'vendor/autoload.php';\n\nuse Parsedown;\n\nfunction convertMarkdownToHtml($input) {\n    $parsedown = new Parsedown();\n\n    // Sanitize input to prevent XSS attacks\n    $safeInput = htmlspecialchars($input, ENT_QUOTES, 'UTF-8');\n\n    // Convert Markdown to HTML\n    $htmlOutput = $parsedown->text($safeInput);\n\n    return $htmlOutput;\n}\n\n// Example usage\n$userInput = "This is a paragraph.\n\nAnd this is another paragraph.\n\n- Item 1\n- Item 2\n\n[Link](https://example.com)";\n$htmlContent = convertMarkdownToHtml($userInput);\necho $htmlContent;\n?>