<?php\n   // Function to search text in indexed content\n   function searchInFiles($searchTerm) {\n       $mysqli = new mysqli("localhost", "user", "password", "database");\n       $stmt = $mysqli->prepare("SELECT file_path FROM file_contents WHERE content LIKE ?");\n       $likeTerm = "%$searchTerm%";\n       $stmt->bind_param('s', $likeTerm);\n       $stmt->execute();\n       $result = $stmt->get_result();\n       \n       $files = [];\n       while ($row = $result->fetch_assoc()) {\n           $files[] = $row['file_path'];\n       }\n       \n       $stmt->close();\n       $mysqli->close();\n       \n       return $files;\n   }\n\n   // Example usage\n   $searchTerm = 'some search term';\n   $foundFiles = searchInFiles($searchTerm);\n   foreach ($foundFiles as $file) {\n       echo "Found in: $file\n";\n   }