<?php\n$decimalNumber = '1.67119535743e+33';\n$integerPart = bcmul($decimalNumber, '1', 0);\n$hexNumber = bcdechex($integerPart);\n\necho $hexNumber;\n\nfunction bcdechex($dec) {\n    $hex = '';\n    while ($dec !== '0') {\n        $last = bcmod($dec, '16');\n        $hex = dechex($last) . $hex;\n        $dec = bcdiv(bcsub($dec, $last), '16', 0);\n    }\n    return $hex ? $hex : '0';\n}\n?>