<?php\n// Load the JPG image\n$jpgImage = imagecreatefromjpeg('path/to/your/image.jpg');\n\nif (!$jpgImage) {\n    die('Failed to load JPG image');\n}\n\n// Save the image as a GIF\n$gifPath = 'path/to/your/image.gif';\nif (!imagegif($jpgImage, $gifPath)) {\n    die('Failed to save GIF image');\n}\n\n// Save the image as a PNG\n$pngPath = 'path/to/your/image.png';\nif (!imagepng($jpgImage, $pngPath)) {\n    die('Failed to save PNG image');\n}\n\n// Save the image as a BMP\n$bmpPath = 'path/to/your/image.bmp';\nif (!imagebmp($jpgImage, $bmpPath)) {\n    die('Failed to save BMP image');\n}\n\n// Free up memory\nimagedestroy($jpgImage);\n\necho "Images have been converted and saved successfully.";\n?>