function quicksort(array $array): array {\n    if (count($array) < 2) {\n        return $array;\n    }\n    \n    $left = $right = [];\n    reset($array);\n    $pivot_key = key($array);\n    $pivot = array_shift($array);\n    \n    foreach ($array as $k => $v) {\n        if ($v < $pivot) {\n            $left[$k] = $v;\n        } else {\n            $right[$k] = $v;\n        }\n    }\n    \n    return array_merge(quicksort($left), array($pivot_key => $pivot), quicksort($right));\n}\n\n// Example usage\n$array = [3, 0, 2, 5, -1, 4, 1];\n$sorted = quicksort($array);\nprint_r($sorted);