$mysqli = new mysqli("your_host", "your_user", "your_password", "your_database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Set lc_time_names to Spanish\n$mysqli->query("SET lc_time_names = 'es_ES'");\n\n$check_sql = 'SELECT personID, name, DATE_FORMAT(persons.birthdate, "%d de %M, %Y") AS formatted_birthdate, birthplace, countryID FROM persons WHERE personID = ?';\n\nif ($stmt = $mysqli->prepare($check_sql)) {\n    $stmt->bind_param('i', $pid);\n    $stmt->execute();\n    $stmt->bind_result($personDB, $name, $birthdate, $birthplace, $countryID);\n    while ($stmt->fetch()) {\n        echo "ID: $personDB, Name: $name, Birthdate: $birthdate, Birthplace: $birthplace, CountryID: $countryID\n";\n    }\n    $stmt->close();\n}\n\n$mysqli->close();