// Function to get the category thumbnail URL\nfunction get_category_thumbnail_url($term_id) {\n    $thumbnail_url = get_term_meta($term_id, 'category_thumbnail', true);\n    if ($thumbnail_url) {\n        return esc_url($thumbnail_url);\n    }\n    return '';\n}\n\n// Example usage in a category template\n$category = get_queried_object();\n$thumbnail_url = get_category_thumbnail_url($category->term_id);\nif ($thumbnail_url) {\n    echo '<img src="' . $thumbnail_url . '" alt="' . esc_attr($category->name) . '">';\n}