function getWeekRange($date) {\n    // Convert the given date to a timestamp\n    $timestamp = strtotime($date);\n\n    // Get the start date of the week (Sunday)\n    $start_date = date('Y-m-d', strtotime('last Sunday', $timestamp));\n\n    // Get the end date of the week (Saturday)\n    $end_date = date('Y-m-d', strtotime('next Saturday', $timestamp));\n\n    // Handle case if the given date is itself a Sunday\n    if (date('w', $timestamp) == 0) {\n        $start_date = date('Y-m-d', $timestamp);\n        $end_date = date('Y-m-d', strtotime('next Saturday', $timestamp));\n    }\n\n    return array($start_date, $end_date);\n}\n\n// Example usage\n$date = '2009-05-01';\nlist($start_date, $end_date) = getWeekRange($date);\necho "Start Date: $start_date\n";\necho "End Date: $end_date\n";