// application/classes/controller/Ajax.php\nclass Controller_Ajax extends Controller {\n\n    public function action_login() {\n        // Handle login AJAX request\n        $response = array('status' => 'success', 'message' => 'Logged in successfully');\n        $this->response->body(json_encode($response));\n    }\n\n    public function action_editprofile() {\n        // Handle edit profile AJAX request\n        $response = array('status' => 'success', 'message' => 'Profile updated');\n        $this->response->body(json_encode($response));\n    }\n}