$hostname = 'localhost';\n$username = 'crb';\n$password = 'letmein';\n$login = 'your_login'; // Assign appropriate values to these variables\n$clave = 'your_clave'; // Assign appropriate values to these variables\n\ntry {\n    $cn = new PDO("mysql:host=$hostname;dbname=bitacora", $username, $password);\n    echo 'Connected to database<br />';\n\n    $sql = "SELECT * FROM usuario WHERE login = :login AND clave = :clave";\n    $stmt = $cn->prepare($sql);\n    $stmt->bindParam(':login', $login);\n    $stmt->bindParam(':clave', $clave);\n\n    $stmt->execute();\n\n    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        print $row['login'] . ' - ' . $row['clave'] . '<br />';\n    }\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n} catch (Exception $e) {\n    echo 'Error: ' . $e->getMessage();\n}