$group_id = 55;\n\n// Create a subquery for gs_relations\n$gs_relations = new GSRelations();\n$subquery = $gs_relations->select()\n                         ->from('gs_relations', 'subscriber_id')\n                         ->where('group_id = ?', $group_id);\n\n// Create the main query for subscribers\n$subscribers = new Subscribers();\n$select = $subscribers->select()\n                      ->setIntegrityCheck(false)\n                      ->where('id IN ?', $subquery);\n\n// Fetch results\n$result = $subscribers->fetchAll($select);