<?php\ntry {\n    $dbh = new PDO('mysql:host=localhost;dbname=testdb', $user, $pass);\n\n    // Using bindParam\n    $stmt = $dbh->prepare("INSERT INTO products (id, name, price) VALUES (NULL, :name, :price)");\n    $name = 'Test Product';\n    $price = '20.5';  // Notice this is a string\n    \n    // Bind parameters with specified data types\n    $stmt->bindParam(':name', $name, PDO::PARAM_STR);\n    $stmt->bindParam(':price', $price, PDO::PARAM_INT); // This will not convert $price to integer\n\n    // Execute the statement\n    $stmt->execute();\n\n    // Change the variables and execute again\n    $name = 'Another Product';\n    $price = '30.0';\n    $stmt->execute();\n\n    // Using bindValue\n    $stmt = $dbh->prepare("INSERT INTO products (id, name, price) VALUES (NULL, :name, :price)");\n    $name = 'Third Product';\n    $price = 50; // This is an integer\n\n    // Bind values with specified data types\n    $stmt->bindValue(':name', $name, PDO::PARAM_STR);\n    $stmt->bindValue(':price', $price, PDO::PARAM_INT); // This will not convert $price to integer\n\n    // Execute the statement\n    $stmt->execute();\n\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>