<?php\n// Database connection\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Prepare and bind\n$stmt = $mysqli->prepare("INSERT INTO calendar (event, from_time, to_time, day) VALUES (?, ?, ?, ?)");\n$stmt->bind_param("ssss", $event, $from_time, $to_time, $date_event);\n\n// Set parameters and execute\n$event = $_REQUEST['event'];\n$from_time = $_REQUEST['from_time'];\n$to_time = $_REQUEST['to_time'];\n$date_event = $_REQUEST['date_event'];\n\n$stmt->execute();\n\necho "New records created successfully";\n\n$stmt->close();\n$mysqli->close();\n?>