<?php\nfunction extractDimensions($input) {\n    // Use a regular expression to match floating point numbers\n    preg_match_all('/\d+(\.\d+)?/', $input, $matches);\n    return array_map('floatval', $matches[0]);\n}\n\n// Test cases\n$input1 = '152.15 x 12.34 x 11mm';\n$input2 = '152.15x12.34x11 mm';\n$input3 = '152.15mmx12.34mm x 11mm';\n\n$dim1 = extractDimensions($input1);\n$dim2 = extractDimensions($input2);\n$dim3 = extractDimensions($input3);\n\n// Output the results\nprint_r($dim1);\nprint_r($dim2);\nprint_r($dim3);\n?>