<?php\nrequire 'vendor/autoload.php';\n\nuse Zend\Mail\Message;\nuse Zend\Mail\Transport\Smtp;\nuse Zend\Mail\Transport\SmtpOptions;\nuse Zend\Mail\Protocol\Pop3;\nuse Zend\Mail\Protocol\Smtp\Auth\Login as SmtpAuthLogin;\nuse Zend\Mail\Transport\Smtp as SmtpTransport;\n\n// POP3 Server Configuration\n$pop3Host = 'pop3.example.com';\n$pop3Port = 110;\n$pop3User = 'your_pop3_username';\n$pop3Pass = 'your_pop3_password';\n\n// SMTP Server Configuration\n$smtpHost = 'smtp.example.com';\n$smtpPort = 587;\n$smtpUser = 'your_smtp_username';\n$smtpPass = 'your_smtp_password';\n\n// Step 1: Authenticate with the POP3 server\n$pop3 = new Pop3();\n$pop3->connect($pop3Host, $pop3Port);\n$pop3->login($pop3User, $pop3Pass);\n\n// Step 2: Prepare the email message\n$message = new Message();\n$message->setFrom('from@example.com')\n        ->addTo('to@example.com')\n        ->setSubject('Test Subject')\n        ->setBody('This is the body of the email.');\n\n// Step 3: Configure the SMTP transport\n$options = new SmtpOptions([\n    'host' => $smtpHost,\n    'port' => $smtpPort,\n    'connection_class' => 'login',\n    'connection_config' => [\n        'username' => $smtpUser,\n        'password' => $smtpPass,\n    ],\n]);\n\n$transport = new SmtpTransport();\n$transport->setOptions($options);\n\n// Step 4: Send the email\n$transport->send($message);\n\n// Step 5: Disconnect from the POP3 server\n$pop3->logout();\n$pop3->disconnect();