<?php\n$html = <<<HTML\n<html>\n  <head></head>\n  <body>\n    <h2>Title1</h2>\n    <p>Paragraph1</p>\n    <p>Paragraph2</p>\n    <h2>Title2</h2>\n    <p>Paragraph3</p>\n  </body>\n</html>\nHTML;\n\n$dom = new DOMDocument;\nlibxml_use_internal_errors(true); // Suppress errors for invalid HTML\n$dom->loadHTML($html);\nlibxml_clear_errors();\n\n$xpath = new DOMXPath($dom);\n$elements = $xpath->query('//h2 | //p');\n\nforeach ($elements as $element) {\n    echo $element->nodeValue . "\n";\n}\n?>