<?php\nfunction haversineGreatCircleDistance($latitudeFrom, $longitudeFrom, $latitudeTo, $longitudeTo, $earthRadius = 6371000) {\n  // Convert from degrees to radians\n  $latFrom = deg2rad($latitudeFrom);\n  $lonFrom = deg2rad($longitudeFrom);\n  $latTo = deg2rad($latitudeTo);\n  $lonTo = deg2rad($longitudeTo);\n\n  $latDelta = $latTo - $latFrom;\n  $lonDelta = $lonTo - $lonFrom;\n\n  $angle = 2 * asin(sqrt(pow(sin($latDelta / 2), 2) +\n    cos($latFrom) * cos($latTo) * pow(sin($lonDelta / 2), 2)));\n  return $angle * $earthRadius;\n}\n\n// Your current location\n$currentLat = 40.7128; // example latitude\n$currentLong = -74.0060; // example longitude\n\n// List of places with their latitudes and longitudes\n$places = [\n  ['lat' => 40.7127, 'long' => -74.0059],\n  ['lat' => 40.7138, 'long' => -74.0070],\n  // Add more places here\n];\n\n$nearbyDistance = 100; // 100 meters\n\nforeach ($places as $place) {\n  $distance = haversineGreatCircleDistance($currentLat, $currentLong, $place['lat'], $place['long']);\n  if ($distance <= $nearbyDistance) {\n    echo "You are within 100 meters of a place.\n";\n    // You can break the loop or process further if needed\n  }\n}\n?>