class Controller_Plugin_ViewSetup extends Zend_Controller_Plugin_Abstract\n{\n    public function dispatchLoopStartup(Zend_Controller_Request_Abstract $request)\n    {\n        $view = Zend_Layout::getMvcInstance()->getView();\n        \n        // Set the content type\n        $view->headMeta()->setHttpEquiv('Content-Type', 'text/html;charset=utf-8');\n        \n        // Set the global keywords\n        $globalKeywords = 'global,generic,keywords,';\n        $view->headMeta()->setName('keywords', $globalKeywords);\n    }\n}\n\n// In your view script\n$globalKeywords = 'global,generic,keywords,';\n$viewSpecificKeywords = 'view,specific,keywords';\n\n// Merge global and view-specific keywords\n$combinedKeywords = $globalKeywords . $viewSpecificKeywords;\n\n// Remove the trailing comma if necessary\n$combinedKeywords = rtrim($combinedKeywords, ',');\n\n// Set the combined keywords\n$this->headMeta()->setName('keywords', $combinedKeywords);