<?php\n// Assuming $this->id and $user are already defined\n$id = $this->id;\n$user = $this->username; // Or however you get the username\n\n$query = "SELECT id FROM votes WHERE username = :username AND article_id = :article_id";\n$stmt = $pdo->prepare($query);\n$stmt->bindParam(':username', $user, PDO::PARAM_STR);\n$stmt->bindParam(':article_id', $id, PDO::PARAM_INT);\n$stmt->execute();\n\n$result = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n// Process the result as needed\n?>