<?php\n\ninterface Animal {\n    public function makeSound();\n}\n\nclass Dog implements Animal {\n    public function makeSound() {\n        echo "Woof";\n    }\n}\n\nclass Cat implements Animal {\n    public function makeSound() {\n        echo "Meow";\n    }\n}\n\nfunction animalSound(Animal $animal) {\n    $animal->makeSound();\n}\n\n$dog = new Dog();\n$cat = new Cat();\n\nanimalSound($dog); // Outputs: Woof\nanimalSound($cat); // Outputs: Meow\n\n?>