<?php\n// Define the short and long options\n$shortopts  = "a:b:c"; // Short options with values\n$longopts  = array(\n    "alpha:", // Long options with values\n    "beta:", \n    "charlie"\n);\n\n// Parse options\n$options = getopt($shortopts, $longopts);\n\n// Print parsed options\nprint_r($options);\n\n// Handle the parsed options\nif (isset($options['a'])) {\n    echo "Value for -a: " . $options['a'] . "\n";\n}\n\nif (isset($options['b'])) {\n    echo "Value for -b: " . $options['b'] . "\n";\n}\n\nif (isset($options['c'])) {\n    echo "-c is set\n";\n}\n?>