<?php\n$category_id = get_cat_ID('teen'); // Get the category ID\n$query = new WP_Query(array(\n    'cat' => $category_id,\n    'post_type' => 'post'\n));\n\nif ($query->have_posts()) {\n    while ($query->have_posts()) {\n        $query->the_post();\n        get_template_part('template-parts/content', get_post_format());\n    }\n    wp_reset_postdata();\n} else {\n    echo 'No posts found';\n}\n?>