<?php\n$arr_nav = array(\n    array("id" => "apple", "url" => "apple.html", "name" => "My Apple"),\n    array("id" => "orange", "url" => "orange/oranges.html", "name" => "View All Oranges"),\n    array("id" => "pear", "url" => "pear.html", "name" => "A Pear")\n);\n\n$v_url_root = "/"; // Replace with your root URL if needed\n\nforeach ($arr_nav as $index => $item) {\n    $class = "";\n    if ($index === 0) {\n        $class = "first";\n    } elseif ($index === count($arr_nav) - 1) {\n        $class = "last";\n    }\n\n    echo '<li id="' . $item['id'] . '" class="' . $class . '">';\n    echo '<a href="' . $v_url_root . $item['url'] . '" title="' . $item['name'] . '">' . $item['name'] . '</a>';\n    echo '</li>' . "\r\n";\n}\n?>