// Database connection\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n// Fetch column names for tabx\n$resultX = $mysqli->query("SHOW COLUMNS FROM tabx");\n$columnsX = [];\nwhile ($row = $resultX->fetch_assoc()) {\n    $columnsX[] = "tabx." . $row['Field'] . " AS 'tabx_" . $row['Field'] . "'";\n}\n\n// Fetch column names for taby\n$resultY = $mysqli->query("SHOW COLUMNS FROM taby");\n$columnsY = [];\nwhile ($row = $resultY->fetch_assoc()) {\n    $columnsY[] = "taby." . $row['Field'] . " AS 'taby_" . $row['Field'] . "'";\n}\n\n// Combine column names\n$allColumns = array_merge($columnsX, $columnsY);\n$columnsList = implode(", ", $allColumns);\n\n// Construct the final SQL query\n$sql = "SELECT $columnsList FROM tabx, taby WHERE ... ;";  // Your join condition\n\n// Execute the query\n$result = $mysqli->query($sql);\n\n// Fetch and display results\nwhile ($row = $result->fetch_assoc()) {\n    print_r($row);\n}\n\n// Close connection\n$mysqli->close();