session_start();\n\n   // Example: Marking a post as read\n   function markPostAsRead($postId) {\n       if (isset($_SESSION['unread_posts'][$postId])) {\n           unset($_SESSION['unread_posts'][$postId]);\n       }\n   }\n\n   // Example: Checking if a forum has unread posts\n   function isForumUnread($forumId) {\n       foreach ($_SESSION['unread_posts'] as $post) {\n           if ($post['forum_id'] == $forumId) {\n               return true;\n           }\n       }\n       return false;\n   }\n\n   // Usage\n   markPostAsRead($somePostId);\n   if (isForumUnread($someForumId)) {\n       echo "Forum has unread posts";\n   }