function create_excerpt($text, $length = 100) {\n    // Strip HTML tags\n    $text = strip_tags($text);\n\n    // Truncate text to the specified length\n    if (strlen($text) > $length) {\n        $text = substr($text, 0, $length);\n        // Ensure it doesn't cut off in the middle of a word\n        $text = substr($text, 0, strrpos($text, ' '));\n        $text .= '...';\n    }\n    \n    return $text;\n}\n\n// Example usage\n$full_content = "<p>This is a sample content from the RSS feed. It contains HTML tags.</p>";\n$excerpt = create_excerpt($full_content, 50);\necho $excerpt;