// Load the Paginator component in your controller\n$this->loadComponent('Paginator');\n\n// Configure the pagination settings\n$paginationSettings = [\n    'conditions' => ['Collection.uid' => $uid],\n    'contain' => ['Entity'],\n    'limit' => 10, // Number of entities per page\n    'order' => ['Entity.name' => 'asc'] // Order by entity name\n];\n\n// Retrieve the collection with paginated entities\n$collection = $this->Collection->find('first', [\n    'conditions' => ['Collection.uid' => $uid],\n    'contain' => ['Entity']\n]);\n\n// Paginate the entities\n$entities = $this->Paginator->paginate($this->Collection->Entities, $paginationSettings);\n\n// Pass the data to the view\n$this->set(compact('collection', 'entities'));