<?php\n// Assuming you have already fetched $row from the database\n\n// Check if the form is submitted\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    // Check if the file input is empty\n    if (empty($_FILES['image']['name'])) {\n        $image_caption = $row['image_caption'];\n    } else {\n        // Handle the file upload\n        $image = str_replace(' ', '_', $_FILES['image']['name']);\n        // Move the file to the upload folder and rename it\n        move_uploaded_file($_FILES['image']['tmp_name'], UPLOAD_DIR . $image);\n        $image_caption = $image;\n    }\n\n    // Now you can update the database with the new or existing image caption\n    $sql = "UPDATE clients SET image_caption = ? WHERE cliente_id = ?";\n    $stmt = $conn->prepare($sql);\n    $stmt->bind_param('si', $image_caption, $_POST['cliente_id']);\n    $stmt->execute();\n\n    // Redirect or handle the response as needed\n    header("Location: your_redirect_url.php");\n    exit;\n}\n?>