class Item extends AppModel {\n    public function isOwnedByUser($user_id, $item_id) {\n        // Perform the necessary query to check if the item is owned by the user\n        // Example query (adjust according to your HABTM relationship)\n        return $this->UserItem->hasAny(array(\n            'UserItem.user_id' => $user_id,\n            'UserItem.item_id' => $item_id\n        ));\n    }\n}