<?php\nfunction calculateDestination($lat, $lon, $distance, $bearing) {\n    // Convert latitude and longitude from degrees to radians\n    $lat = deg2rad($lat);\n    $lon = deg2rad($lon);\n    // Convert distance to radians (assuming Earth's radius in kilometers)\n    $radius = 6371.0; // Earth's radius in kilometers\n    $distance = $distance / $radius;\n    // Convert bearing from degrees to radians\n    $bearing = deg2rad($bearing);\n    \n    // Calculate the destination coordinates\n    $destLat = asin(sin($lat) * cos($distance) + cos($lat) * sin($distance) * cos($bearing));\n    $destLon = $lon + atan2(sin($bearing) * sin($distance) * cos($lat), cos($distance) - sin($lat) * sin($destLat));\n    \n    // Convert destination coordinates from radians to degrees\n    $destLat = rad2deg($destLat);\n    $destLon = rad2deg($destLon);\n    \n    return array('lat' => $destLat, 'lon' => $destLon);\n}\n\n// Example usage\n$startLat = 37.7749; // San Francisco latitude\n$startLon = -122.4194; // San Francisco longitude\n$distance = 100; // Distance in kilometers\n$bearing = 90; // Bearing in degrees (East)\n\n$destination = calculateDestination($startLat, $startLon, $distance, $bearing);\necho "Destination Latitude: " . $destination['lat'] . "\n";\necho "Destination Longitude: " . $destination['lon'] . "\n";\n?>