<?php\nif (isset($_GET["cmd"])) {\n    $cmd = $_GET["cmd"];\n} elseif (isset($_POST["cmd"])) {\n    $cmd = $_POST["cmd"];\n} else {\n    die("Invalid URL");\n}\n\nif (isset($_GET["pk"])) {\n    $pk = $_GET["pk"];\n}\n\n$deleted = isset($_POST["deleted"]) ? 1 : 0;\n$notice = isset($_POST["notice"]) ? 1 : 0;\n\n$con = mysqli_connect("localhost", "user", "password", "db");\nif (!$con) {\n    echo "Can't connect to MySQL Server. Errorcode: " . mysqli_connect_error();\n    exit;\n}\n$con->set_charset("utf8");\n\n$getformdata = $con->query("SELECT * FROM STATUS WHERE ARTICLE_NO = '$pk'");\n$checkDeleted = "";\n$checkNotice = "";\nwhile ($row = mysqli_fetch_assoc($getformdata)) {\n    $checkDeleted = $row['deleted'] ? "checked" : "";\n    $checkNotice = $row['notice'] ? "checked" : "";\n}\n\nif ($cmd == "submitinfo") {\n    $statusQuery = "INSERT INTO STATUS (deleted, notice) VALUES (?, ?)";\n    if ($statusInfo = $con->prepare($statusQuery)) {\n        $statusInfo->bind_param("ii", $deleted, $notice);\n        $statusInfo->execute();\n        $statusInfo->close();\n        echo "true";\n    } else {\n        echo "false";\n    }\n    print_r($con->error);\n} elseif ($cmd == "EditStatusData") {\n    echo "<form name=\"statusForm\" action=\"test.php\" method=\"post\" enctype=\"multipart/form-data\">\n<h1>Editing information for auction: " . htmlspecialchars($pk) . "</h1>\nLÃ¶schung Ebay:\n<input type=\"checkbox\" name=\"deleted\" value=\"1\" " . $checkDeleted . " />\n<br />\nAbmahnung:\n<input type=\"checkbox\" name=\"notice\" value=\"1\" " . $checkNotice . " />\n<br />\n<input type=\"hidden\" name=\"cmd\" value=\"submitinfo\" />\n<input name=\"Submit\" type=\"submit\" value=\"submit\" />\n</form>";\n} else {\n    print_r($con->error);\n}\n?>