<?php\nclass foo {\n    public $title;\n\n    public function __construct($title = "") {\n        $this->title = $title;\n\n        echo "<html>\n";\n        echo "<head>\n";\n        echo "<title>".$this->title."</title>\n";\n        echo "</head>\n";\n        echo "<body>\n";\n    }\n\n    /** \n    * I get $title from index.php\n    */\n    public function setTitle($title) {\n        $this->title = $title;\n    }\n\n    public function __destruct() {\n        echo "</body>\n";\n        echo "</html>\n";\n    }\n}\n\n// Usage example:\n$myPage = new foo("My Page Title");\n// If you need to change the title later, you can still use:\n$myPage->setTitle("New Title");\n?>