class ShoppingCart {\n    var $items = array();\n\n    function add_item($id) {\n        $this->items[$id] = new myitem($id);\n    }\n\n    function remove_item($id) {\n        if (isset($this->items[$id])) {\n            unset($this->items[$id]);\n            return true;\n        } else {\n            return false;\n        }\n    }\n\n    function get_count() {\n        return count($this->items);\n    }\n}\n\nclass myitem {\n    var $id;\n    \n    function __construct($id) {\n        $this->id = $id;\n    }\n}\n\n// Example usage:\n$cart = new ShoppingCart();\n$cart->add_item(1);\n$cart->add_item(2);\n$cart->remove_item(1);\necho $cart->get_count(); // Output should be 1