<?php\n// Database connection\n$pdo = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n\n// Get the article\n$article_id = 1; // Example article ID, you can change it based on your needs\n$query = $pdo->prepare("SELECT content FROM articles WHERE id = :id");\n$query->execute(['id' => $article_id]);\n$article = $query->fetch(PDO::FETCH_ASSOC)['content'];\n\n// Parameters\n$words_per_page = isset($_GET['words_per_page']) ? (int) $_GET['words_per_page'] : 500;\n$page = isset($_GET['page']) ? (int) $_GET['page'] : 1;\n\n// Function to split article into pages\nfunction paginate_article($content, $words_per_page) {\n    $words = explode(' ', $content);\n    $pages = [];\n    $page = "";\n    $word_count = 0;\n\n    foreach ($words as $word) {\n        $page .= $word . ' ';\n        $word_count++;\n\n        if ($word_count >= $words_per_page && substr($word, -1) == '.') {\n            // Ensure we split at the end of a sentence for better readability\n            $pages[] = trim($page);\n            $page = "";\n            $word_count = 0;\n        }\n    }\n\n    // Add the last remaining words to the last page\n    if (!empty(trim($page))) {\n        $pages[] = trim($page);\n    }\n\n    return $pages;\n}\n\n$pages = paginate_article($article, $words_per_page);\n$total_pages = count($pages);\n\n// Display the requested page\nif ($page < 1 || $page > $total_pages) {\n    echo "Page not found.";\n    exit;\n}\n\necho "<h1>Article Title</h1>"; // You can fetch and display the article title\necho "<p>" . nl2br($pages[$page - 1]) . "</p>";\n\n// Pagination controls\nif ($page > 1) {\n    echo '<a href="display.php?page=' . ($page - 1) . '&words_per_page=' . $words_per_page . '">Previous</a> ';\n}\nif ($page < $total_pages) {\n    echo '<a href="display.php?page=' . ($page + 1) . '&words_per_page=' . $words_per_page . '">Next</a>';\n}\n?>