class ItemsController extends AppController {\n    public function index() {\n        // Assuming $user_id is available (e.g., from session)\n        $user_id = $this->Auth->user('id');\n        \n        // Fetch paginated items\n        $this->paginate = array('limit' => 10); // Adjust based on your requirements\n        $items = $this->paginate('Item');\n        \n        // Check ownership and add to the item data\n        foreach ($items as &$item) {\n            // Add the ownership status to each item\n            $item['Item']['isOwnedByUser'] = $this->Item->isOwnedByUser($user_id, $item['Item']['id']);\n        }\n        \n        // Pass data to the view\n        $this->set(compact('items'));\n    }\n}