<?php\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n$fileId = $_GET['file_id'];\n$result = $mysqli->query("SELECT * FROM media_files WHERE id = $fileId");\n$file = $result->fetch_assoc();\n\nif ($file) {\n    $serverId = $file['server_id'];\n    $serverResult = $mysqli->query("SELECT * FROM media_servers WHERE id = $serverId");\n    $server = $serverResult->fetch_assoc();\n    \n    $fileUrl = $server['server_url'] . '/' . $file['file_name'];\n    // Redirect to the file URL\n    header("Location: $fileUrl");\n} else {\n    echo "File not found.";\n}\n?>